import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import DaContainer from './DaContainer';
import Home from '../components/Home';


const MainContainer = () => {

  const [url, setUrl] = useState("/main_page_screen.wav")
  const [audio, setAudio] = useState(new Audio(url))

  const changeUrl = (url) => {
    setUrl(url)
  }

  const playAudio = () => {
    audio.play()
  }

  const pauseAudio = () => {
    audio.pause()
  }


  useEffect(() => {
    setAudio(new Audio(url))
  }, [url])

  return (
    <>
    <Router>
    <button onClick={playAudio}>Play</button>
      <button onClick={pauseAudio}>Pause</button>
      <Switch>
        <Route exact path="/" render ={() => {return <Home playAudio={playAudio}/>}} />
        <Route path="/mode" render ={() => {return <DaContainer changeUrl={changeUrl} playAudio={playAudio}/>}} />
      </Switch>
    </Router>
    </>
  )
}

export default MainContainer;
